    private void tblListadoMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblListadoMouseClicked
        // TODO add your handling code here:

        int fila = tblListado.getSelectedRow();

        // Crear objeto cliente
        Cliente cliente = new Cliente();
        cliente.setIdCliente(Integer.parseInt(tblListado.getValueAt(fila, 0).toString()));
        cliente.setNombre(tblListado.getValueAt(fila, 1).toString());

        // Actualizar en vista reserva
        ViewReserva.cliente = cliente;
        ViewReserva.refreshView();

        // Cerrar dialogo
        this.dispose();
    }//GEN-LAST:event_tblListadoMouseClicked


